mapper.select:
SELECT id, `name`, `code`, `industry_code`,  `industry_name`,  `pe_dynamic`,  `pe_lyr`,  `pe_ttm`,  `pb` FROM
stock


    `stock`.name LIKE CONCAT('%', #{name} ,'%')


    `stock`.code LIKE CONCAT('%', #{code} ,'%')


    `stock`.industry_name = #{industryName}


ORDER BY CONVERT(`${sort}`,SIGNED) ${order}
LIMIT #{offSet}, #{pageRow}

SELECT count(*) FROM
stock


    `stock`.name LIKE CONCAT('%', #{name} ,'%')


    `stock`.code = #{code}


    `stock`.industry_name = #{industryName}

SELECT ANY_VALUE(`id`) AS `id`, industry_name, COUNT(industry_name) as acount, FORMAT(SUM(pe_ttm)/COUNT(industry_name), 2) AS pe_ttm , FORMAT(SUM(pe_lyr)/COUNT(industry_name), 2) AS pe_lyr , FORMAT(SUM(pb)/COUNT(industry_name), 2) AS pb FROM stock GROUP BY `industry_name`
HAVING `stock`.industry_name = #{industryName}
ORDER BY ${sort} ${order} LIMIT #{offSet}, #{pageRow}

SELECT COUNT(*) FROM ( SELECT COUNT(*) FROM stock GROUP BY `industry_name`
HAVING `stock`.industry_name = #{industryName}
) u
